mapper.select:
SELECT ho.id,ho.time_in,ho.time_out,ho.is_service,o.id oid,o.name,h.id hid,ho.time,orgDy.id orgid,orgDy.name orgname,h.fir_type,
case h.fir_type
when 2 then c.level
when 4 then hd.name
when 5 then org.name
else h.sec_type
end sec_type
FROM home_oldman ho left join organ orgDy on ho.organ_id=orgDy.id and orgDy.disable=0
,oldman_base o,organ jw,auto_value x,home h left join chx c on c.id=h.sec_type left join organ org on org.id=h.sec_type left join home_doctor hd on hd.id=h.sec_type
WHERE ho.oldman_id=o.id AND x.id=o.xq_id AND jw.id=x.parent_index and ho.home_id=h.id and o.disable=0

    and o.id=#{entity.oldmanId}


    and o.id=#{entity.oldman.id}


    and h.fir_type IN

        #{item}



    and jw.id in (
    SELECT
    case o.parent
    when 0 then child.id
    else o.id
    end id
    from organ o left join organ child on child.parent=o.id
    WHERE o.id=#{entity.organId}
    )


    AND ho.home_id=#{entity.homeId}


    and ( orgDy.name LIKE  concat('%',#{entity.search},'%') OR hd.name LIKE  concat('%',#{entity.search},'%') OR org.name LIKE  concat('%',#{entity.search},'%')
    OR h.sec_type LIKE  concat('%',#{entity.search},'%')
    OR o.name LIKE  concat('%',#{entity.search},'%') OR o.id=#{entity.search})


    ORDER BY ${sortType}


    ${sort}


    limit #{start},#{length}

SELECT count(1) FROM home_oldman ho left join organ orgDy on ho.organ_id=orgDy.id and orgDy.disable=0 ,oldman_base o,organ jw,auto_value x,home h left join chx c on c.id=h.sec_type left join organ org on org.id=h.sec_type left join home_doctor hd on hd.id=h.sec_type WHERE ho.oldman_id=o.id AND x.id=o.xq_id AND jw.id=x.parent_index and ho.home_id=h.id and o.disable=0
and o.id=#{entity.oldmanId}
and h.fir_type IN
#{item}
and jw.id in ( SELECT case o.parent when 0 then child.id else o.id end id from organ o left join organ child on child.parent=o.id WHERE o.id=#{entity.organId} )
AND ho.home_id=#{entity.homeId}
and ( orgDy.name LIKE concat('%',#{entity.search},'%') OR hd.name LIKE concat('%',#{entity.search},'%') OR org.name LIKE concat('%',#{entity.search},'%') OR h.sec_type LIKE concat('%',#{entity.search},'%') OR o.name LIKE concat('%',#{entity.search},'%') OR o.id=#{entity.search})

mapper.delete:
DELETE from home_oldman

    where oldman_id in (
    SELECT o.id from oldman_base o,auto_value x,organ org WHERE o.xq_id=x.id and x.parent_index=org.id and org.id=#{jwId}
    )
