mapper.select:
SELECT o.id,o.time,old.id oid,old.name,o.blood_type,e.value eyesight,i.value intelligence,ha.type,ha.desc,hs.fir_type,hs.sec_type_name,ha.id haid,hs.id hsid FROM oldman_base old,organ org,auto_value x, oldman_health_base o LEFT JOIN health_add ha ON ha.oldman_id=o.oldman_id LEFT JOIN health_select_oldman hso on hso.oldman_id=o.oldman_id left join health_select hs on hso.health_select_id=hs.id left join auto_value e on e.id=o.eyesight left join auto_value i on i.id=o.intelligence WHERE old.id=o.oldman_id AND x.id=old.xq_id AND org.id=x.parent_index
and old.id in( select a.id from (select oo.id from oldman_base oo where oo.id>0 order by oo.${sortType} ${sort} limit #{start},#{length} ) as a )
and o.oldman_id=#{entity.oldman.id}
and org.id=#{entity.organId}
and old.id in ( SELECT DISTINCT(old.id) FROM oldman_base old,oldman_health_base o LEFT JOIN health_add ha ON ha.oldman_id=o.oldman_id LEFT JOIN health_select_oldman hso on hso.oldman_id=o.oldman_id left join health_select hs on hso.health_select_id=hs.id WHERE old.id=o.oldman_id and (o.blood_type LIKE concat('%',#{entity.search},'%') OR o.eyesight LIKE concat('%',#{entity.search},'%') OR old.name LIKE concat('%',#{entity.search},'%') OR o.intelligence LIKE concat('%',#{entity.search},'%') OR ha.desc LIKE concat('%',#{entity.search},'%') OR hs.sec_type_name LIKE concat('%',#{entity.search},'%')) )
order by o.${sortType} ${sort}

SELECT count(1)
FROM oldman_health_base ohb,oldman_base ob,organ org,auto_value x
WHERE ohb.oldman_id=ob.id AND x.id=ob.xq_id AND org.id=x.parent_index

    and oldman_id=#{entity.oldmanId}


    and org.id=#{entity.organId}

mapper.update:
UPDATE oldman_base
SET name=#{name},sex=#{sex},birthday=#{birthday},pid=#{pid},address=#{address},jw_id=#{jwId},census=#{census},political_status#{politicalStatus},
district_id=#{districtId},phone=#{phone}
WHERE id=#{id}

update oldman_health_base
SET
blood_type= CASE oldman_id

    WHEN ${item.oldmanId} THEN #{item.bloodType}

END,
eyesight= CASE oldman_id

    WHEN ${item.oldmanId} THEN #{item.eyesight}

END,
intelligence= CASE oldman_id

    WHEN ${item.oldmanId} THEN #{item.intelligence}

END,
is_mb= CASE oldman_id

    WHEN ${item.oldmanId} THEN #{item.isMb}

END,
is_sn= CASE oldman_id

    WHEN ${item.oldmanId} THEN #{item.isSn}

END,
is_ywfy= CASE oldman_id

    WHEN ${item.oldmanId} THEN #{item.isYwfy}

END
where oldman_id in

    #{item.oldmanId,jdbcType=BIGINT}
