mapper.select:
SELECT oo.id,o.id oid,o.name,oo.time_in,oo.time_out,oo.num,oo.time,oo.noexist_name,oo.apply_time
FROM organ_oldman oo LEFT join oldman_base o on oo.oldman_id=o.id
WHERE oo.organ_id=#{entity.organ.id}

    AND oo.num='0'


    AND oo.num!='0'


    AND (oo.oldman_id is NOT NULL or oo.oldman_id!=0)


    AND (oo.oldman_id is NULL or oo.oldman_id=0)


    and ( o.pid LIKE  concat('%',#{entity.search},'%') OR oo.noexist_pid LIKE  concat('%',#{entity.search},'%')
    OR oo.noexist_name LIKE  concat('%',#{entity.search},'%')
    OR o.name LIKE  concat('%',#{entity.search},'%') OR o.id=#{entity.search})


    ORDER BY ${sortType}


    ${sort}


    limit #{start},#{length}

SELECT oo.id,org.id orgid,o.id oid,oo.time_in,oo.time_out,oo.num,oo.time,o.name,org.name orgname,oo.apply_time
FROM organ_oldman oo, oldman_base o,organ org,organ_type ot,organ jw,auto_value x
WHERE oo.oldman_id=o.id AND org.id=oo.organ_id AND org.organ_type_id=ot.id AND x.id=o.xq_id AND jw.id=x.parent_index and o.disable=0
and org.disable=0

    and oo.organ_id=#{entity.organ.id}


    AND ot.fir_type=#{entity.firType}


    AND oo.num='0'


    AND oo.num!='0'


    and jw.id in (
    SELECT
    case o.parent
    when 0 then child.id
    else o.id
    end id
    from organ o left join organ child on child.parent=o.id
    WHERE o.id=#{entity.organId}
    )


    and o.id=#{entity.oldman.id}


    and ( org.name LIKE  concat('%',#{entity.search},'%')
    OR o.name LIKE  concat('%',#{entity.search},'%') OR o.id=#{entity.search})


    ORDER BY ${sortType}


    ${sort}


    limit #{start},#{length}

SELECT count(1) FROM organ_oldman oo,oldman_base o,organ org,organ_type ot,organ jw,auto_value x WHERE oo.oldman_id=o.id AND org.id=oo.organ_id AND org.organ_type_id=ot.id AND x.id=o.xq_id AND jw.id=x.parent_index and o.disable=0 and org.disable=0
and oo.organ_id=#{entity.organ.id}
AND ot.fir_type=#{entity.firType}
and jw.id in ( SELECT case o.parent when 0 then child.id else o.id end id from organ o left join organ child on child.parent=o.id WHERE o.id=#{entity.organId} )
and ( org.name LIKE concat('%',#{entity.search},'%') OR o.name LIKE concat('%',#{entity.search},'%') OR o.id=#{entity.search})
