mapper.select:
SELECT uo.organ_id
FROM user_organ uo,sysuser u
WHERE uo.user_id=u.id AND u.username=#{username}

SELECT u.id,u.username,u.password,r.desc,u.time
FROM sysuser u,sysrole r,user_role ur
WHERE u.id=ur.user_id and r.id=ur.role_id

    ORDER BY ${sortType}


    ${sort}


    limit #{start},#{length}
