mapper.delete:
DELETE from volunteer
WHERE oldman_id IN

    #{item}

DELETE from volunteer
where oldamn_id in (
SELECT o.id FROM oldman_base o,auto_value x,organ org
WHERE o.xq_id=x.id and x.parent_index=org.id

     and org.id=#{orgaId}

)

mapper.select:
SELECT v.id,ob.id oid,ob.name,v.time
FROM volunteer v,oldman_base ob,organ org,auto_value x
WHERE ob.id=v.oldman_id AND x.id=ob.xq_id AND org.id=x.parent_index and ob.disable=0

    and org.id in (
    SELECT
    case o.parent
    when 0 then child.id
    else o.id
    end id
    from organ o left join organ child on child.parent=o.id
    WHERE o.id=#{entity.organId}
    )


    ORDER BY ob.${sortType}


    ${sort}


    limit #{start},#{length}
