mapper.select:
SELECT ho.id,ho.time_in,ho.time_out,o.id oid,o.name,h.id hid,ho.time,h.fir_type,
case h.fir_type
when 2 then c.level
when 4 then hd.name
when 5 then org.name
else h.sec_type
end sec_type
FROM home_oldman ho,oldman_base o,organ jw,auto_value x,home h left join chx c on c.id=h.sec_type left join organ org on org.id=h.sec_type left join home_doctor hd on hd.id=h.sec_type
WHERE ho.oldman_id=o.id AND x.id=o.xq_id AND jw.id=x.parent_index and ho.home_id=h.id

    AND jw.id=#{entity.jwId}


    AND ho.home_id=#{entity.homeId}

ORDER BY ${sortType} ${sort}
limit #{start},#{length}

SELECT count(1)
FROM home_oldman
WHERE home_id=#{entity.homeId}
