mapper.select:
SELECT ob.id,ob.name,ob.goal
FROM oldman_base ob,organ org,auto_value x
WHERE x.id=ob.xq_id AND org.id=x.parent_index AND ob.goal > ${entity.keyGoalBase}

    and org.id=#{entity.jwId}

SELECT count(1)
FROM oldman_base ob,organ org,auto_value x
WHERE x.id=ob.xq_id AND org.id=x.parent_index AND ob.goal > ${entity.keyGoalBase}

    and org.id=#{entity.jwId}
