mapper.select:
SELECT id,name
FROM organ
WHERE organ_type_id=#{type}

SELECT o.id,o.name,o.intro,o.address,o.phone,o.num,o.num_in,o.num_remain,o.status,ot.sec_type organType,d.value districtName,o.time
FROM organ o LEFT JOIN auto_value d on d.id=o.district_id,organ_type ot
WHERE o.organ_type_id=ot.id AND ot.fir_type=#{entity.organFirTypeId}

    AND o.status=#{entity.status}

SELECT count(1)
FROM organ o,organ_type ot
WHERE o.organ_type_id=ot.id AND ot.fir_type=#{entity.organFirTypeId}
