mapper.select:
SELECT oo.id,org.id orgid,o.id oid,oo.time_in,oo.time_out,oo.num,oo.time,o.name,org.name orgname
FROM organ_oldman oo,oldman_base o,organ org,organ_type ot,organ jw,auto_value x
WHERE oo.oldman_id=o.id AND org.id=oo.organ_id AND org.organ_type_id=ot.id AND x.id=o.xq_id AND jw.id=x.parent_index

    and oo.organ_id in (
    SELECT
    case o.parent
    when 0 then child.id
    else o.id
    end id
    from organ o left join organ child on child.parent=o.id
    WHERE o.id=#{entity.organId}
    )


    AND ot.fir_type=#{entity.firType}


    and jw.id=#{entity.organId}


    and o.id=#{entity.oldman.id}


    ORDER BY ${sortType}


    ${sort}


    limit #{start},#{length}

SELECT count(1) FROM organ_oldman oo,oldman_base o,organ org,organ_type ot,organ jw,auto_value x WHERE oo.oldman_id=o.id AND org.id=oo.organ_id AND org.organ_type_id=ot.id AND x.id=o.xq_id AND jw.id=x.parent_index
and oo.organ_id in ( SELECT case o.parent when 0 then child.id else o.id end id from organ o left join organ child on child.parent=o.id WHERE o.id=#{entity.organId} )
AND ot.fir_type=#{entity.firType}
and jw.id=#{entity.organId}
